import { INIT_APPLICATION } from '../actions/application.actions'
import manifest from '../../../app.json'

const { expo: { name, version } } = manifest

const initialState = {
    status: false,
    version: -1,
}

const applicationReducer = async(state = initialState, action) => {
    switch (action.type) {
        case INIT_APPLICATION: {
            try{
                await AsyncStorage.setItem('status','true');
                await AsyncStorage.setItem('version',JSON.stringify(version));
                await AsyncStorage.setItem('name',JSON.stringify(name));
            }catch(error){
                console.log(error);
            }

            // return {
            //     status: true,
            //     version,
            //     name,
            // }
        }

        default: {
            return state
        }
    }
}

export default applicationReducer
